mapper.select:
select a.id, a.name, u.name as owner, a.startDate, a.endDate from tbl_activity a inner join tbl_user u on u.id = a.owner
a.name like '%' #{name} '%'
and u.name like '%' #{owner} '%'
and a.startDate > #{startDate}
and a.endDate < #{endDate}
order by a.createTime desc limit #{skipCount},#{pageSize}

select count(*) from tbl_activity a inner join tbl_user u on u.id = a.owner
a.name like '%' #{name} '%'
and u.name like '%' #{owner} '%'
and a.startDate > #{startDate}
and a.endDate < #{endDate}

mapper.delete:
delete from tbl_activity
where id in

    #{id}
